import React, { useRef, useEffect, useState } from 'react';
import Chart from 'chart.js/auto'; // Import Chart from Chart.js library

function PieChart({ character1, character2, characterDetails }) {
  const chartRef = useRef(null);
  const [chartInstance, setChartInstance] = useState(null);
  const [delayedInit, setDelayedInit] = useState(false);

  useEffect(() => {
    console.log('Character 1:', character1);
    console.log('Character 2:', character2);
    console.log('Character Details:', characterDetails);

    const countVisionData = () => {
      const dataCounts = { Pyro: 0, Cryo: 0, Hydro: 0, Electro: 0, Anemo: 0, Geo: 0 };
      [character1, character2].forEach(characterName => {
        const character = characterDetails.find(char => char.name === characterName);
        if (character) {
          dataCounts[character.vision]++;
        }
      });
      return dataCounts;
    };

    const dataCounts = countVisionData();

    if (chartInstance) {
      chartInstance.data.datasets[0].data = Object.values(dataCounts);
      chartInstance.update();
    } else if (delayedInit) {
      const ctx = chartRef.current?.getContext('2d');
      const newChartInstance = new Chart(ctx, {
        type: 'pie',
        data: {
          labels: ['Pyro', 'Cryo', 'Hydro', 'Electro', 'Anemo', 'Geo'],
          datasets: [
            {
              data: Object.values(dataCounts),
              backgroundColor: ['orangered', 'white', 'aqua', 'purple', 'green', 'darkgoldenrod'],
            },
          ],
        },
        options: {
          plugins: {
            legend: {
              display: true,
              position: 'top',
            },
            title: {
              display: true,
              text: 'Vision Distribution',
            },
          },
        },
      });
      setChartInstance(newChartInstance);
    }

    return () => {
      if (chartInstance) {
        chartInstance.destroy();
      }
    };
  }, [character1, character2, characterDetails, chartInstance, delayedInit]);

  useEffect(() => {
    // Delay chart initialization to ensure proper cleanup
    const timeout = setTimeout(() => {
      setDelayedInit(true);
    }, 0);

    return () => clearTimeout(timeout);
  }, [character1, character2, characterDetails]);

  return (
    <div className="pie-chart-container">
      <div style={{ padding: '20px', width: '50%', margin: 'auto' }}>
        <canvas ref={chartRef} />
      </div>
    </div>
  );
}

export default PieChart;
