import React, { useRef, useEffect } from 'react';
import { Chart as ChartJS, CategoryScale, LinearScale, PointElement, Legend, Tooltip, PolarAreaController, ArcElement } from 'chart.js';

ChartJS.register(CategoryScale, LinearScale, PointElement, Legend, Tooltip, PolarAreaController, ArcElement);

function PolarAreaChart({ character1, character2, characterDetails }) {
  const chartRef = useRef(null);

  useEffect(() => {
    let chartInstance = null;

    const countRarityData = (characterName) => {
      const dataCounts = { '4 stars': 0, '5 stars': 0 };

      const character = characterDetails.find(char => char.name === characterName);
      if (character) {
        if (Array.isArray(character.rarity)) {
          character.rarity.forEach(rarity => {
            dataCounts[rarity + ' stars'] += 1; // Increment count for the respective rarity
          });
        } else {
          dataCounts[character.rarity + ' stars'] += 1; // Increment count for the single rarity
        }
      }
      return dataCounts;
    };

    const rarityData1 = countRarityData(character1);
    const rarityData2 = countRarityData(character2);

    if (chartRef.current && (Object.keys(rarityData1).length !== 0 || Object.keys(rarityData2).length !== 0)) {
      if (chartInstance) {
        chartInstance.destroy();
      }

      const labels = ['4 Stars', '5 Stars'];
      const datasets = [];

      if (Object.keys(rarityData1).length !== 0) {
        datasets.push({
          label: character1,
          data: [rarityData1['4 stars'], rarityData1['5 stars']],
          backgroundColor: 'rgba(128, 0, 128, 0.5)', // Purple
          borderColor: 'rgba(128, 0, 128, 1)', // Purple border
          borderWidth: 2,
        });
      }

      if (Object.keys(rarityData2).length !== 0) {
        datasets.push({
          label: character2,
          data: [rarityData2['4 stars'], rarityData2['5 stars']],
          backgroundColor: 'rgba(255, 215, 0, 0.5)', // Gold
          borderColor: 'rgba(255, 215, 0, 1)', // Gold border
          borderWidth: 2,
        });
      }

      chartInstance = new ChartJS(chartRef.current.getContext('2d'), {
        type: 'polarArea',
        data: {
          labels: labels,
          datasets: datasets,
        },
        options: {
          plugins: {
            legend: {
              display: true,
              position: 'top',
              labels: {
                color: 'rgba(255, 255, 255, 1)', // White border color for labels
              },
            },
          },
        },
      });
    }

    return () => {
      if (chartInstance) {
        chartInstance.destroy();
      }
    };
  }, [character1, character2, characterDetails]);

  return (
    <div className="polar-area-chart-container">
      <div style={{ width: '70%', height: '300px', padding: '20px', marginLeft: '30%', marginTop: '3%' }}>
        <canvas ref={chartRef} />
      </div>
    </div>
  );
}

export default PolarAreaChart;
