import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Timeline from '@mui/lab/Timeline';
import TimelineItem from '@mui/lab/TimelineItem';
import TimelineSeparator from '@mui/lab/TimelineSeparator';
import TimelineConnector from '@mui/lab/TimelineConnector';
import TimelineContent from '@mui/lab/TimelineContent';
import TimelineDot from '@mui/lab/TimelineDot';

function AlternateTimeline() {
  const [characterDetails, setCharacterDetails] = useState([]);
  const [selectedProperty, setSelectedProperty] = useState('');
  const [propertyValues, setPropertyValues] = useState([]);

  useEffect(() => {
    const fetchCharacterDetails = async () => {
      try {
        const response = await axios.get('https://genshinlist.com/api/characters');
        setCharacterDetails(response.data);
        // Assuming the API provides birthday data as 'birthday' field in character details
        setSelectedProperty('birthday'); // Default property to display
        if (response.data.length > 0) {
          setPropertyValues(response.data.map(character => character.birthday)); // Initial property values
        }
      } catch (error) {
        console.error('Error fetching character details:', error);
      }
    };

    fetchCharacterDetails();
  }, []);

  const handlePropertyChange = (event) => {
    const selectedProperty = event.target.value;
    setSelectedProperty(selectedProperty);
    // Update property values based on the selected property
    setPropertyValues(characterDetails.map(character => character[selectedProperty]));
  };

  return (
    <div className='page' style={{ backgroundColor: '#192536', color: 'white', position: 'relative' }}>
      <h6 style={{ textAlign: 'center', marginBottom: '10px' }}>Timeline</h6>
      <div style={{ marginBottom: '20px' }}>
        <select value={selectedProperty} onChange={handlePropertyChange}>
          <option value="">Select property</option>
          {Object.keys(characterDetails[0] || {}).map(property => (
            <option key={property} value={property}>
              {property}
            </option>
          ))}
        </select>
      </div>
      <Timeline position="alternate">
        {propertyValues.map((value, index) => (
          <TimelineItem key={index}>
            <TimelineSeparator>
              <TimelineDot />
              {index < propertyValues.length - 1 && <TimelineConnector />}
            </TimelineSeparator>
            <TimelineContent>{value || 'Unknown'}</TimelineContent>
          </TimelineItem>
        ))}
      </Timeline>
    </div>
  );
}

export default AlternateTimeline;
