import React, { useRef, useEffect } from 'react';
import { Chart as ChartJS, BarElement, CategoryScale, LinearScale, Tooltip, Legend } from 'chart.js';

ChartJS.register(BarElement, CategoryScale, LinearScale, Tooltip, Legend);

function BarChart({ character1, character2, characterDetails }) {
  const chartRef = useRef(null);

  useEffect(() => {
    let chartInstance = null;

    const character1Data = characterDetails.find(character => character.name === character1);
    const character2Data = characterDetails.find(character => character.name === character2);

    if (chartRef.current && character1Data && character2Data) {
      const character1RarityCounts = {
        '4 stars': Array.isArray(character1Data.rarity) ? character1Data.rarity.filter(rarity => rarity === 4).length : 0,
        '5 stars': Array.isArray(character1Data.rarity) ? character1Data.rarity.filter(rarity => rarity === 5).length : 0,
      };
      const character2RarityCounts = {
        '4 stars': Array.isArray(character2Data.rarity) ? character2Data.rarity.filter(rarity => rarity === 4).length : 0,
        '5 stars': Array.isArray(character2Data.rarity) ? character2Data.rarity.filter(rarity => rarity === 5).length : 0,
      };

      chartInstance = new ChartJS(chartRef.current.getContext('2d'), {
        type: 'bar',
        data: {
          labels: ['4 Stars', '5 Stars'],
          datasets: [
            {
              label: character1,
              data: [character1RarityCounts['4 stars'], character1RarityCounts['5 stars']],
              backgroundColor: 'rgba(255, 99, 132, 0.5)', 
              borderColor: 'rgba(255, 99, 132, 1)', 
              borderWidth: 1,
            },
            {
              label: character2,
              data: [character2RarityCounts['4 stars'], character2RarityCounts['5 stars']],
              backgroundColor: 'rgba(54, 162, 235, 0.5)', 
              borderColor: 'rgba(54, 162, 235, 1)', 
              borderWidth: 1,
            },
          ],
        },
        options: {
          scales: {
            x: {
              grid: {
                display: false, // Hide the x-axis grid lines
              },
            },
            y: {
              beginAtZero: true, // Start y-axis scale from zero
            },
          },
          plugins: {
            legend: {
              display: true,
              position: 'top',
            },
          },
        },
      });
    }

    return () => {
      // Cleanup function to destroy the chart when the component unmounts or before rendering a new one
      if (chartInstance) {
        chartInstance.destroy();
      }
    };
  }, [character1, character2, characterDetails]);

  return (
    <div className="bar-chart-container">
      <div style={{ width: '50%', height: '500px', margin: 'auto' }}>
        <canvas ref={chartRef} />
      </div>
    </div>
  );
}

export default BarChart;
