import { useState } from 'react';
import PieChart from "../components/PieChart";
import LineChart from "../components/LineChart";
import BarChart from "../components/BarChart";

function Comparison() {
  const [selectedCharacter, setSelectedCharacter] = useState('Diluc');

  const handleCharacterChange = (character) => {
    setSelectedCharacter(character);
  };

  return (
    <div className='page' style={{ backgroundColor: '#192536', color: 'white', display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
      <div style={{ display: 'flex', marginBottom: '20px' }}>
        <button onClick={() => handleCharacterChange('Diluc')} style={{ marginRight: '10px' }}>Diluc</button>
        <button onClick={() => handleCharacterChange('Kayea')} style={{ marginRight: '10px' }}>Kayea</button>
        <button onClick={() => handleCharacterChange('Lisa')} style={{ marginRight: '10px' }}>Lisa</button>
        <button onClick={() => handleCharacterChange('Jean')} style={{ marginRight: '10px' }}>Jean</button>
      </div>
      <div style={{ display: 'flex', width: '100%', justifyContent: 'space-between' }}>
        <div style={{ flex: 1 }}>
          <PieChart character={selectedCharacter} />
        </div>
        <div style={{ flex: 1 }}>
          <LineChart character={selectedCharacter} />
        </div>
        <div style={{ flex: 1 }}>
          <BarChart character={selectedCharacter} />
        </div>
      </div>
    </div>
  );
}

export default Comparison;
