import { Chart as ChartJS, ArcElement, Tooltip, Legend } from 'chart.js';
import { Pie } from 'react-chartjs-2';

ChartJS.register(ArcElement, Tooltip, Legend);

function PieChart({ selectedCharacters, characterDetails }) {
  // Function to calculate element counts for each selected character
  const calculateElementCounts = () => {
    const elementCounts = {
      Pyro: 0,
      Cryo: 0,
      Hydro: 0,
      Electro: 0,
      Anemo: 0,
      Geo: 0,
    };

    selectedCharacters.forEach(character => {
      const characterData = characterDetails.find(char => char.name === character);
      if (characterData) {
        elementCounts[characterData.element]++;
      }
    });

    return Object.values(elementCounts);
  };

  const data = {
    labels: ['Pyro', 'Cryo', 'Hydro', 'Electro', 'Anemo', 'Geo'],
    datasets: [
      {
        data: calculateElementCounts(),
        backgroundColor: ['orangered', 'white', 'aqua','purple','green','darkgoldenrod'],
      },
    ],
  };

  const options = {
    plugins: {
      legend: {
        display: true,
        position: 'top',
      },
      title: {
        display: true,
        text: 'Element Distribution for Selected Characters',
      },
    },
  };

  return (
    <div className="App" style={{
      backgroundColor: 'transparent'
    }}>
      <div
        style={{
          padding: '20px',
          width: '15%',
          marginTop: '5%',
          marginLeft: '40%',
        }}
      >
        <Pie data={data} options={options} />
      </div>
    </div>
  );
}

export default PieChart;
