import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Timeline.css';
import { VerticalTimeline, VerticalTimelineElement }  from 'react-vertical-timeline-component';
import 'react-vertical-timeline-component/style.min.css';

function Timeline() {
  const [characterDetails, setCharacterDetails] = useState(null);

  useEffect(() => {
    axios.get('https://genshinlist.com/api/characters')
      .then(response => {
        const dilucCharacter = response.data.find(character => character.name === 'Diluc');
        if (dilucCharacter) {
          setCharacterDetails(dilucCharacter);
          console.log('Character Details:', dilucCharacter); // Added console log
        } else {
          console.log('Character not found');
        }
      })
      .catch(error => {
        console.log(error);
      });
  }, []);

  const renderTimeline = () => {
    if (!characterDetails) return null;

    const { birthday, name } = characterDetails;
    const birthYear = new Date(birthday).getFullYear();
    const currentYear = new Date().getFullYear();
    const age = currentYear - birthYear;

    const timelineItems = [];
    for (let year = birthYear; year <= currentYear; year++) {
      const itemText = `${year}: ${name}'s ${age}th birthday`;
      timelineItems.push(
        <VerticalTimelineElement
          key={year}
          date={`${year}`}
          iconStyle={{ background: 'rgb(33, 150, 243)', color: '#fff' }}
          icon={<i className="fas fa-birthday-cake"></i>}
        >
          <h3 className="vertical-timeline-element-title">{itemText}</h3>
        </VerticalTimelineElement>
      );
    }

    return (
      <div>
        <h3>Birthday Timeline for {name}</h3>
        <VerticalTimeline>
          {timelineItems}
        </VerticalTimeline>
      </div>
    );
  };

  return (
    <div className='page' style={{ backgroundColor: '#192536', color: 'white', padding: '20px', margin: '0'}}>
      {renderTimeline()}
    </div>
  );
}

export default Timeline;
