import { useState, useEffect } from 'react';
import axios from 'axios';
import PieChart from "../components/PieChart";
import LineChart from "../components/LineChart";
import BarChart from "../components/BarChart";

function Comparison() {
  const [selectedCharacters, setSelectedCharacters] = useState([]);
  const [characterDetails, setCharacterDetails] = useState([]);

  useEffect(() => {
    const fetchCharacterDetails = async () => {
      try {
        const response = await axios.get('https://genshinlist.com/api/characters');
        setCharacterDetails(response.data);
      } catch (error) {
        console.error('Error fetching character details:', error);
      }
    };

    fetchCharacterDetails();
  }, []);

  const handleCharacterChange = (character) => {
    if (selectedCharacters.includes(character)) {
      setSelectedCharacters(selectedCharacters.filter(item => item !== character));
    } else {
      setSelectedCharacters([...selectedCharacters, character]);
    }
  };

  return (
    <div className='page' style={{ backgroundColor: '#192536', color: 'white' }}>
      <div style={{ marginBottom: '20px' }}>
        {characterDetails.map(character => (
          <label key={character.name} style={{ fontSize: '14px' }}>
            <input type="checkbox" checked={selectedCharacters.includes(character.name)} onChange={() => handleCharacterChange(character.name)} /> {character.name}
          </label>
        ))}
      </div>
      {selectedCharacters.length === 2 ? (
        <>
          Comparison for {selectedCharacters[0]} vs {selectedCharacters[1]}
          <PieChart character1={selectedCharacters[0]} character2={selectedCharacters[1]} characterDetails={characterDetails} />
          <LineChart character1={selectedCharacters[0]} character2={selectedCharacters[1]} characterDetails={characterDetails} />
          <BarChart character1={selectedCharacters[0]} character2={selectedCharacters[1]} characterDetails={characterDetails} />
        </>
      ) : (
        <div>Please select two characters for comparison.</div>
      )}
    </div>
  );
}

export default Comparison;
