import { useState } from 'react';
import PieChart from "../components/PieChart";
import LineChart from "../components/LineChart";
import BarChart from "../components/BarChart";

function Comparison() {
  const [selectedCharacters, setSelectedCharacters] = useState([]);

  const handleCharacterChange = (character) => {
    if (selectedCharacters.includes(character)) {
      setSelectedCharacters(selectedCharacters.filter(item => item !== character));
    } else {
      setSelectedCharacters([...selectedCharacters, character]);
    }
  };

  return (
    <div className='page' style={{ backgroundColor: '#192536', color: 'white' }}>
      <div style={{ marginBottom: '20px' }}>
        <label style={{ fontSize: '14px' }}>
          <input type="checkbox" checked={selectedCharacters.includes('Diluc')} onChange={() => handleCharacterChange('Diluc')} /> Diluc
        </label>
        <label style={{ fontSize: '14px' }}>
          <input type="checkbox" checked={selectedCharacters.includes('Kayea')} onChange={() => handleCharacterChange('Kayea')} />  Kaeya
        </label>
        <label style={{ fontSize: '14px' }}>
          <input type="checkbox" checked={selectedCharacters.includes('Lisa')} onChange={() => handleCharacterChange('Lisa')} /> Lisa
        </label>
        {/* Add more characters here */}
        {/* Example: */}
        <label style={{ fontSize: '14px' }}>
          <input type="checkbox" checked={selectedCharacters.includes('Keqing')} onChange={() => handleCharacterChange('Keqing')} /> Keqing
        </label>
        <label style={{ fontSize: '14px' }}>
          <input type="checkbox" checked={selectedCharacters.includes('Klee')} onChange={() => handleCharacterChange('Klee')} /> Klee
        </label>
        {/* Add other characters similarly */}
      </div>
      {selectedCharacters.length === 2 ? (
        <>
          Comparison for {selectedCharacters[0]} vs {selectedCharacters[1]}
          <PieChart character1={selectedCharacters[0]} character2={selectedCharacters[1]} />
          <LineChart character1={selectedCharacters[0]} character2={selectedCharacters[1]} />
          <BarChart character1={selectedCharacters[0]} character2={selectedCharacters[1]} />
        </>
      ) : (
        <div>Please select two characters for comparison.</div>
      )}
    </div>
  );
}

export default Comparison;
