import React, { useState } from 'react';
import Confetti from 'react-confetti'; // Import Confetti component
import Features from '../components/Features';
import HeaderImage from '../components/Header'; // Import the header image component

function Home() {
  const [selectedCharacter, setSelectedCharacter] = useState(null);

  // Function to handle the change event of the dropdown menu
  const handleCharacterChange = (event) => {
    const selectedCharacter = event.target.value;
    // Here you can fetch the description of the selected character from your data source
    // For demonstration purposes, let's assume the descriptions are stored in an object
    const descriptions = {
      character1: "Description of character 1",
      character2: "Description of character 2",
      // Add descriptions for other characters as needed
    };
    setSelectedCharacter(descriptions[selectedCharacter]);
  };

  return (
    <div className='page' style={{ backgroundColor: '#192536', color: 'white', position: 'relative' }}>
      <Confetti width={window.innerWidth} height={window.innerHeight} numberOfPieces={100} gravity={0.2} wind={0.1} friction={0.9} confettiSize={6} opacity={0.9} /> {/* Add Confetti component */}
      <HeaderImage /> {/* Include the header image component */}
      <Features />
      {/* Dropdown section */}
      <div style={{ marginTop: '50px', textAlign: 'center' }}>
        <select onChange={handleCharacterChange} value={selectedCharacter}>
          <option value="">Select a character</option>
          <option value="character1">Character 1</option>
          <option value="character2">Character 2</option>
          {/* Add options for other characters as needed */}
        </select>
        {/* Display the description of the selected character */}
        {selectedCharacter && (
          <div style={{ marginTop: '20px' }}>
            <h2>Description:</h2>
            <p>{selectedCharacter}</p>
          </div>
        )}
      </div>
    </div>
  );
}

export default Home;
