import React, { useState, useEffect } from 'react';
import axios from 'axios';
import PieChart from "../components/PieChart";
import PolarAreaChartChart from "../components/PolarAreaChart";
import BarChart from "../components/BarChart";
import paimonImage from "../components/paimon2.png";
import polearm from "../assets/polearm.webp";
import sword from "../assets/sword.webp";
import bow from "../assets/bow.webp";
import catalyst from "../assets/catalyst.webp";
import claymore from "../assets/claymore.webp";
import sumeru from "../assets/sumeru.webp";
import snezhnaya from "../assets/snezhnaya.webp";
import outlander from "../assets/outlander.webp";
import unknown from "../assets/unknown.webp";
import traveler from "../assets/traveler.webp";

function Comparison() {
  const [characterDetails, setCharacterDetails] = useState([]);
  const [selectedCharacter1, setSelectedCharacter1] = useState(null);
  const [selectedCharacter2, setSelectedCharacter2] = useState(null);

  useEffect(() => {
    const fetchCharacterDetails = async () => {
      try {
        const response = await axios.get('https://api.genshin.dev/characters/');
        const characterData = await Promise.all(response.data.map(async (charName) => {
          const charResponse = await axios.get(`https://api.genshin.dev/characters/${charName}`);
          const character = charResponse.data;
          character.icon = `https://api.genshin.dev/characters/${charName}/icon-big`;
          character.card = `https://api.genshin.dev/characters/${charName}/card`;
          character.element = `https://api.genshin.dev/elements/${character.vision.toLowerCase()}/icon`;
          character.nationData = `https://api.genshin.dev/nations/${character.nation.toLowerCase()}/icon`;

          switch (character.weapon.toLowerCase()) {
            case "polearm":
              character.weaponImg = polearm;
              break;
            case "sword":
              character.weaponImg = sword;
              break;
            case "bow":
              character.weaponImg = bow;
              break;
            case "catalyst":
              character.weaponImg = catalyst;
              break;
            case "claymore":
              character.weaponImg = claymore;
              break;
            default:
              character.weaponImg = null;
          }

          switch (character.nation.toLowerCase()) {
            case "sumeru":
              character.nationImg = sumeru;
              break;
            case "snezhnaya":
              character.nationImg = snezhnaya;
              break;
            case "outlander":
              character.nationImg = outlander;
              break;
            case "unknown":
              character.nationImg = unknown;
              break;
            default:
              character.nationImg = character.nationData;
          }

          return character;
        }));
        setCharacterDetails(characterData);
      } catch (error) {
        console.error('Error fetching character details:', error);
      }
    };

    fetchCharacterDetails();
  }, []);

  const handleCharacterSelection1 = (event) => {
    const selectedCharacter = characterDetails.find(character => character.name === event.target.value);
    setSelectedCharacter1(selectedCharacter);
  };

  const handleCharacterSelection2 = (event) => {
    const selectedCharacter = characterDetails.find(character => character.name === event.target.value);
    setSelectedCharacter2(selectedCharacter);
  };

  return (
    <div className='page' style={{ backgroundColor: '#192536', color: 'white' }}>
      <div style={{ marginBottom: '20px', marginTop: '150px' }}>
        <select onChange={handleCharacterSelection1}>
          <option value="">Select first character</option>
          {characterDetails.map(character => (
            <option key={character.name} value={character.name}>
              {character.name}
            </option>
          ))}
        </select>
        <select onChange={handleCharacterSelection2}>
          <option value="">Select second character</option>
          {characterDetails.map(character => (
            <option key={character.name} value={character.name}>
              {character.name}
            </option>
          ))}
        </select>
      </div>
      {(selectedCharacter1 && selectedCharacter2) ? (
        <>
          <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
            <div style={{ margin: '0 20px' }}>
              <img src={selectedCharacter1.icon} alt={selectedCharacter1.name} style={{ width: '200px', height: 'auto' }} />
              <p>{selectedCharacter1.name}</p>
            </div>
            <div style={{ margin: '0 20px' }}>
              <img src={selectedCharacter2.icon} alt={selectedCharacter2.name} style={{ width: '200px', height: 'auto' }} />
              <p>{selectedCharacter2.name}</p>
            </div>
          </div>
          <PieChart character1={selectedCharacter1.name} character2={selectedCharacter2.name} characterDetails={characterDetails} />
          <PolarAreaChartChart character1={selectedCharacter1.name} character2={selectedCharacter2.name} characterDetails={characterDetails} />
          <BarChart character1={selectedCharacter1.name} character2={selectedCharacter2.name} characterDetails={characterDetails} />
        </>
      ) : (
        <div>Please select two characters for comparison.</div>
      )}
      <img src={paimonImage} alt="Paimon" style={{ position: 'absolute', bottom: 440, marginLeft: '-35%', width: '200px', height: 'auto' }} />
    </div>
  );
}

export default Comparison;
