import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Confetti from 'react-confetti';
import Timeline from '@mui/lab/Timeline';
import TimelineItem from '@mui/lab/TimelineItem';
import TimelineSeparator from '@mui/lab/TimelineSeparator';
import TimelineConnector from '@mui/lab/TimelineConnector';
import TimelineContent from '@mui/lab/TimelineContent';
import TimelineDot from '@mui/lab/TimelineDot';

function AlternateTimeline() {
  const [characterDetails, setCharacterDetails] = useState([]);
  const [selectedCharacter1, setSelectedCharacter1] = useState(null);
  const [selectedCharacter2, setSelectedCharacter2] = useState(null);
  const [character1Birthday, setCharacter1Birthday] = useState(null);
  const [character2Birthday, setCharacter2Birthday] = useState(null);

  useEffect(() => {
    const fetchCharacterDetails = async () => {
      try {
        const response = await axios.get('https://genshinlist.com/api/characters');
        const characters = response.data;
        setCharacterDetails(characters);

        // Find the earliest birthday among all characters
        let earliestBirthday = null;
        for (const character of characters) {
          const birthdayDate = parseDate(character.birthday);
          if (!earliestBirthday || birthdayDate < earliestBirthday) {
            earliestBirthday = birthdayDate;
          }
        }

        // Set birthdays based on earliest birthday
        setCharacter1Birthday(earliestBirthday);
        setCharacter2Birthday(earliestBirthday);
      } catch (error) {
        console.error('Error fetching character details:', error);
      }
    };

    fetchCharacterDetails();
  }, []);

  const parseDate = (dateString) => {
    const dateParts = dateString.split('-');
    if (dateParts.length === 3) {
      const year = parseInt(dateParts[0], 10);
      const month = parseInt(dateParts[1], 10) - 1; // Month is 0-indexed
      const day = parseInt(dateParts[2], 10);
      const date = new Date(year, month, day);
      if (!isNaN(date.getTime())) {
        return date;
      }
    }
    // If parsing fails, return null or a default date
    return null;
  };

  const handleCharacterSelection1 = (event) => {
    const selectedCharacter = event.target.value;
    setSelectedCharacter1(selectedCharacter);
    const character = characterDetails.find(character => character.name === selectedCharacter);
    if (character) {
      setCharacter1Birthday(parseDate(character.birthday));
    }
  };

  const handleCharacterSelection2 = (event) => {
    const selectedCharacter = event.target.value;
    setSelectedCharacter2(selectedCharacter);
    const character = characterDetails.find(character => character.name === selectedCharacter);
    if (character) {
      setCharacter2Birthday(parseDate(character.birthday));
    }
  };

  return (
    <div className='page' style={{ backgroundColor: '#192536', color: 'white', position: 'relative' }}>
      <Confetti width={window.innerWidth} height={window.innerHeight} numberOfPieces={100} gravity={0.2} wind={0.1} friction={0.9} confettiSize={6} opacity={0.7} />
      <h6 style={{ textAlign: 'center', marginBottom: '10px' }}>Birthday Timeline</h6>
      <div style={{ marginBottom: '20px' }}>
        <select onChange={handleCharacterSelection1}>
          <option value="">Select first character</option>
          {characterDetails.map(character => (
            <option key={character.name} value={character.name}>
              {character.name}
            </option>
          ))}
        </select>
        <select onChange={handleCharacterSelection2}>
          <option value="">Select second character</option>
          {characterDetails.map(character => (
            <option key={character.name} value={character.name}>
              {character.name}
            </option>
          ))}
        </select>
      </div>
      <Timeline position="alternate">
        {character1Birthday && character2Birthday && (
          (character1Birthday < character2Birthday) ? (
            <>
              <TimelineItem>
                <TimelineSeparator>
                  <TimelineDot style={{ backgroundColor: '#F56620' }} />
                  <TimelineConnector style={{ backgroundColor: '#F56620' }} />
                </TimelineSeparator>
                <TimelineContent>{selectedCharacter1 ? `${selectedCharacter1}'s Birthday: ${character1Birthday.toLocaleDateString() || 'Unknown'}` : 'Select a character'}</TimelineContent>
              </TimelineItem>
              <TimelineItem>
                <TimelineSeparator>
                  <TimelineDot style={{ backgroundColor: '#00EAFF' }} />
                  <TimelineConnector style={{ backgroundColor: '#00EAFF' }} />
                </TimelineSeparator>
                <TimelineContent>{selectedCharacter2 ? `${selectedCharacter2}'s Birthday: ${character2Birthday.toLocaleDateString() || 'Unknown'}` : 'Select a character'}</TimelineContent>
              </TimelineItem>
            </>
          ) : (
            <>
              <TimelineItem>
                <TimelineSeparator>
                  <TimelineDot style={{ backgroundColor: '#00EAFF' }} />
                  <TimelineConnector style={{ backgroundColor: '#00EAFF' }} />
                </TimelineSeparator>
                <TimelineContent>{selectedCharacter2 ? `${selectedCharacter2}'s Birthday: ${character2Birthday.toLocaleDateString() || 'Unknown'}` : 'Select a character'}</TimelineContent>
              </TimelineItem>
              <TimelineItem>
                <TimelineSeparator>
                  <TimelineDot style={{ backgroundColor: '#F56620' }} />
                  <TimelineConnector style={{ backgroundColor: '#F56620' }} />
                </TimelineSeparator>
                <TimelineContent>{selectedCharacter1 ? `${selectedCharacter1}'s Birthday: ${character1Birthday.toLocaleDateString() || 'Unknown'}` : 'Select a character'}</TimelineContent>
              </TimelineItem>
            </>
          )
        )}
      </Timeline>
    </div>
  );
}

export default AlternateTimeline;
