import Chart from 'chart.js/auto';
import { Pie } from 'react-chartjs-2';
import 'chartjs-adapter-date-fns';
import 'chartjs-adapter-moment';

// Import Chart.js elements directly to resolve ESLint errors
import { ArcElement, Tooltip, Legend } from 'chart.js';
Chart.register(ArcElement, Tooltip, Legend);

function PieChart({ character1, character2, characterDetails }) {
  const [chartData, setChartData] = useState(null);

  useEffect(() => {
    if (characterDetails && character1 && character2) {
      const character1Data = characterDetails.find(character => character.name === character1);
      const character2Data = characterDetails.find(character => character.name === character2);

      if (character1Data && character2Data) {
        const vision = character1Data.vision;
        const data = {
          labels: [vision, 'Other Visions'],
          datasets: [
            {
              data: [
                (character1Data.vision === character2Data.vision) ? 1 : 0,
                1
              ],
              backgroundColor: [
                (character1Data.vision === character2Data.vision) ? character1Data.element.color : 'gray',
                'lightgray'
              ],
            },
          ],
        };
        setChartData(data);
      }
    }
  }, [character1, character2, characterDetails]);

  const options = {
    plugins: {
      legend: {
        display: true,
        position: 'top',
      },
      title: {
        display: true,
        text: 'Vision Comparison',
      },
    },
  };

  return (
    <div className="App" style={{
      backgroundColor: 'transparent'
    }}>
      <div
        style={{
          padding: '20px',
          width: '15%',
          marginTop: '5%',
          marginLeft: '40%',
        }}
      >
        {chartData && <Pie data={chartData} options={options} />}
      </div>
    </div>
  );
}

export default PieChart;
